import {Layout, Page, TextStyle} from '@shopify/polaris';

const Index = () => (
    <div>
    <Page>
        <Layout>
        <TextStyle variation="positive">
            <p>Sample app using React and Next.js</p>
        </TextStyle>
        </Layout>
    </Page>
    </div>
  );
  
  export default Index;
